import './app.css';   //1. on importe le css
import React from 'react' // on importe React pour la class

//2. creation du component
class App extends React.component{ 
    render(){
        return(
            <div className="app"> 
                    <h1>Hello world!</h1>
            </div>
        )
    }  
}

//3. exporter le component
export default App;




